cardCss:
@import url("https://unpkg.com/tachyons@4.12.0/css/tachyons.min.css");

/* https://fontlibrary.org/en/font/gnu-unifont */
@font-face {
  font-family: UnifontMedium;
  /*src: url(UnifontMedium.woff) format("woff");*/
  src: url("https://cdn.jsdelivr.net/gh/avidrucker/anki-card-test-1/public/UnifontMedium.woff")
    format("woff");
}

/* Anki-only styling to take up all vertical space of the card display window */
html,
body,
div#qa {
  height: 100%;
}

/* Hide scrollbar for Chrome, Safari and Opera */
.invisible-scrollbar::-webkit-scrollbar {
  display: none;
}

/* Hide scrollbar for IE, Edge and Firefox */
.invisible-scrollbar {
  -ms-overflow-style: none; /* IE and Edge */
  scrollbar-width: none; /* Firefox */
}

.shift-up-right {
  position: relative;
  top: -0.1rem;
}

.visually-hidden {
  position: absolute;
  width: 1px;
  height: 1px;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  white-space: nowrap; /* Prevents content from "spilling" */
  border-width: 0;
}

.visually-hidden:hover {
  position: inherit;
  width: auto;
  height: auto;
  margin: 0;
  overflow: visible;
  clip: auto;
  white-space: normal;
  border-width: 0;
}

.off-screen {
  position: absolute;
  left: -9999px;
}

.balance {
  text-wrap: balance;
}

.play-button {
  color: inherit;
  background-color: transparent;
  border: none;
  z-index: inherit;
  cursor: inherit;
  text-shadow: inherit;
  line-height: inherit;
}

:root {
  --dark-green-1: #0f380f;
  --medium-green-1: #306230;
  --green-1: #8bac0f;
  --light-green-1: #9bbc0f;
  --light-gray-1: #cbcbcb;
}

.unifont-medium {
  font-family: "UnifontMedium", mono;
  font-weight: normal;
  font-style: normal;
}

/*.card is primarily for styling the background of both front and back cards*/
.card {
  background-color: var(--light-gray-1);
  overflow-y: hidden;
}

.dark-green-1 {
  color: var(--dark-green-1);
}

.bg-light-green-1 {
  background-color: var(--light-green-1);
}

.b--dark-green-1 {
  border-color: var(--dark-green-1);
}

.b--medium-green-1 {
  border-color: var(--medium-green-1);
}

.medium-green-1 {
  color: var(--medium-green-1);
}

.bg-green-1 {
  background-color: var(--green-1);
}

.bg-light-gray-1 {
  background-color: var(--light-gray-1);
}

.image-wrapper img:hover {
  filter: url(); /* intentionally left blank */
}

.image-wrapper img {
  filter: url(#colorFilter);
}

.image-wrapper {
}

.lh-audio {
  line-height: 2.5rem;
}

.input-8-bit::placeholder {
  color: var(--medium-green-1);
}

.input-8-bit:focus {
  outline: none;
  background-color: var(--light-green-1);
  border-color: var(
    --dark-green-1
  ) !important; /* overrides default Anki styling for input fields */
}

.grow {
  -moz-osx-font-smoothing: grayscale;
  backface-visibility: hidden;
  transform: translateZ(0);
  transition: transform 0.25s ease-out;
}

.grow:hover,
.grow:focus {
  transform: scale(1.05);
}

.grow:active {
  transform: scale(0.9);
}

.append-play-btn-text a::after {
  content: "â–¶";
  color: var(--medium-green-1);
}

.append-play-btn-text svg {
  display: none;
}

/* input-reset input-8-bit f3 w-100 dark-green-1 ba b--medium-green-1 bg-green-1 pa2 */
.input-container input {
  -webkit-appearance: none;
  background-color: var(--green-1);
  color: var(--dark-green-1);
  font-size: 1.5rem;
  padding: 0.5rem;
  border: 1px solid var(--medium-green-1);
}

.input-container input::placeholder {
  color: var(--medium-green-1);
}

.input-container input:focus {
  outline: 0 none;
  background-color: var(--light-green-1);
  border: 1px solid var(--dark-green-1) !important;
}
