/*.card is primarily for styling the background of both front and back cards*/
.card {
  font-family: "Liberation Serif", "DejaVu Serif", "Times New Roman",
    "SF Pro Display", "Segoe UI", system-ui, serif;
}

.v-align-mid {
  vertical-align: middle;
  line-height: 0;
}

.play-button {
  color: inherit;
  background-color: transparent;
  border: none;
  z-index: inherit;
  cursor: inherit;
  text-shadow: inherit;
  line-height: inherit;
  padding: 0;
}

.lh-audio {
  line-height: 2.5rem;
}

.grow-custom > * {
  -moz-osx-font-smoothing: grayscale;
  backface-visibility: hidden;
  transform: translateZ(0);
  transition: transform 0.25s ease-out;
}

.grow-custom > *:hover,
.grow-custom > *:focus {
  transform: scale(1.05);
}

.grow-custom > *:active {
  transform: scale(0.9);
}

/* input-reset input-8-bit f3 w-100 dark-green ba b--medium-green bg-green pa2 */
#input-container input {
  background-color: white;
  font-size: 1.5rem;
  padding: 0.5rem;
  width: 100%;
  text-align: left;
  color: black;
}

