cardCss:
@import url("https://unpkg.com/tachyons@4.12.0/css/tachyons.min.css");

/* Anki-only styling to take up all vertical space of the card display window */
html,
body,
div#qa {
  height: 100%;
}

@import url("https://fonts.googleapis.com/css2?family=DotGothic16&display=swap");

.dotgothic16-regular {
  font-family: "DotGothic16", sans-serif;
  font-weight: 400;
  font-style: normal;
}

/* https://fontlibrary.org/en/font/chicagoflf */
@font-face {
  font-family: ChicagoFLF;
  src: url(ChicagoFLF.ttf) format("truetype");
}

.chicago-flf {
  font-family: "ChicagoFLF", sans-serif;
}

:root {
}

/*.card is primarily for styling the background of both front and back cards*/
.card {
  overflow-y: hidden;
}

.image-wrapper img {
  display: block;
  height: auto;
}

.image-wrapper {
  display: inline-block;
  height: auto;
}

.shift-up-left {
  transform: translate(-0.5rem, -0.25rem);
}

.lh-audio {
  height: 1rem;
}

.bg-checkered {
  background-image: url(Swatch.svg);
  background-repeat: repeat;
}

.dither-5 {
  /* Apply the SVG filter */
  filter: url(#dithering-filter5);
}

.show-on-hover {
  transition: opacity 0.5s ease-in-out;
}
.show-on-hover:hover {
  opacity: 1;
}

.macmouse {
  cursor: url(macmouse.png), auto;
}

.macmouse .macpointer {
  cursor: url("macmouse_pointer.png"), auto;
}

.shift-up-right {
  position: relative;
  top: -0.1rem;
}

.visually-hidden {
  position: absolute;
  width: 1px;
  height: 1px;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  white-space: nowrap; /* Prevents content from "spilling" */
  border-width: 0;
}

.visually-hidden:hover {
  position: inherit;
  width: auto;
  height: auto;
  margin: 0;
  overflow: visible;
  clip: auto;
  white-space: normal;
  border-width: 0;
}

.off-screen {
  position: absolute;
  left: -9999px;
}

.balance {
  text-wrap: balance;
}

.play-button {
  color: inherit;
  background-color: transparent;
  border: none;
  z-index: inherit;
  cursor: inherit;
  text-shadow: inherit;
  line-height: inherit;
  padding: 0.15rem 0.6rem 0.5rem 0.85rem;
}

#input-container input {
  -webkit-appearance: none;
  background-color: white;
  color: black;
  font-size: 1.25rem;
  padding: 0.5rem;
  border: 2px solid gray;
  width: 100%;
  -webkit-appearance: none;
  -moz-appearance: none;
}

#input-container input::placeholder {
  color: gray;
}

#input-container input:focus {
  outline: 0 none;
  background-color: white;
  border: 2px solid black !important;
}

#input-container input::-moz-focus-inner {
  border: 0;
  padding: 0;
}

/* Anki Only Styles*/
.append-play-btn-text a::after {
  content: "▶";
  color: black;
}

.append-play-btn-text svg {
  display: none;
}

.append-play-btn-text button {
  font-size: 1.5rem;
  cursor: url("macmouse_pointer.png"), auto;
}

.append-play-btn-text {
  height: 2.75rem;
}

.replay-button {
  margin: 0;
  font-size: 1.5rem;
}

.f0 {
  font-size: 0;
}

.shift-up-tiny {
  transform: translateY(-0.05rem);
}

.shift-down-children * {
  vertical-align: -5px;
}
