cardCss:
@import url("https://unpkg.com/tachyons@4.12.0/css/tachyons.min.css");

@import url("https://fonts.googleapis.com/css2?family=Courier+Prime:ital,wght@0,400;0,700;1,400;1,700&display=swap");

@import url("https://fonts.googleapis.com/css2?family=M+PLUS+1+Code:wght@100..700&display=swap");

:root {
  --matrix-green: lime;
}

.lime {
  color: lime;
}

/* Anki-only styling to take up all vertical space of the card display window */
html,
body,
div#qa {
  height: 100%;
}

/* Hide scrollbar for Chrome, Safari and Opera */
.invisible-scrollbar::-webkit-scrollbar {
  display: none;
}

/* Hide scrollbar for IE, Edge and Firefox */
.invisible-scrollbar {
  -ms-overflow-style: none; /* IE and Edge */
  scrollbar-width: none; /* Firefox */
}

.visually-hidden {
  position: absolute;
  width: 1px;
  height: 1px;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  white-space: nowrap; /* Prevents content from "spilling" */
  border-width: 0;
}

.visually-hidden:hover {
  position: inherit;
  width: auto;
  height: auto;
  margin: 0;
  overflow: visible;
  clip: auto;
  white-space: normal;
  border-width: 0;
}

.off-screen {
  position: absolute;
  left: -9999px;
}

.play-button {
  color: inherit;
  background-color: transparent;
  border: none;
  z-index: inherit;
  cursor: inherit;
  text-shadow: inherit;
  line-height: inherit;
  padding: 0;
}

.lh-audio {
  line-height: 2.5rem;
}

.grow-custom > * {
  -moz-osx-font-smoothing: grayscale;
  backface-visibility: hidden;
  transform: translateZ(0);
  transition: transform 0.25s ease-out;
}

.grow-custom > *:hover,
.grow-custom > *:focus {
  transform: scale(1.05);
}

.grow-custom > *:active {
  transform: scale(0.9);
}

/* input-reset input-8-bit f3 w-100 dark-green ba b--medium-green bg-green pa2 */
#input-container input {
  background-color: transparent;
  color: lime;
  font-size: 1.5rem;
  padding: 0.5rem;
  border: 1px solid lime;
  width: 100%;
  z-index: 1;
}

#input-container input::placeholder {
  color: lime;
  opacity: 0.6;
}

#input-container input:focus {
  outline: 0 none;
  background-color: transparent;
  border: 1px solid lime !important;
}

#input-container input::-moz-focus-inner {
  border: 0;
  padding: 0;
}

/* Anki Only Styles*/
.append-play-btn-text a::after {
  content: "â–¶";
  color: var(--medium-green);
}

.append-play-btn-text svg {
  display: none;
}

.append-play-btn-text button {
  font-size: 2.5rem;
  padding: 0 0.5rem;
  cursor: pointer;
}

.append-play-btn-text {
  height: 2.5rem;
}

.replay-button {
  margin: 0;
  font-size: 2.5rem;
}

.courier-prime {
  font-family: "Courier Prime", mono;
}

/*.card is primarily for styling the background of both front and back cards*/
.card {
  background-color: black;
  color: lime;
}
.m-plus-1-code {
  font-family: "M PLUS 1 Code", monospace;
  font-optical-sizing: auto;
  font-style: normal;
}

.matrix-green {
  color: var(--matrix-green);
}

.matrix {
  position: relative;
}

.matrix::after {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 255, 0, 0.25);
  mix-blend-mode: color;
}

.matrix:hover::after {
  background: none; /* Remove the background on hover */
  mix-blend-mode: normal; /* Reset mix-blend-mode */
}

.image-overlay {
  position: relative;
  overflow: hidden;
}

.image-overlay img {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%; /* Ensures overlay size matches the base image */
  height: auto;
  display: block; /* Removes any extra space below the image */
}

.image-overlay img:first-child {
  position: relative; /* Keeps the base image in the normal flow */
}

.dimmed {
  filter: brightness(0.15);
}

.img-filter {
  mix-blend-mode: exclusion;
  filter: contrast(1.5);
}

.img-filter:hover {
  mix-blend-mode: normal;
  filter: brightness(1);
}

.lh-0 {
  line-height: 0;
  vertical-align: middle;
}

.child-img-lh-0 img {
  line-height: 0;
  vertical-align: middle;
}

.glowing-text {
  text-shadow:
    0 0 5px rgba(0, 255, 0, 0.15),
    0 0 10px rgba(0, 255, 0, 0.15),
    0 0 15px rgba(0, 255, 0, 0.15),
    0 0 20px rgba(0, 255, 0, 0.15),
    0 0 25px rgba(0, 255, 0, 0.15),
    0 0 30px rgba(0, 255, 0, 0.15),
    0 0 5px rgba(0, 255, 0, 0.15),
    0 0 10px rgba(0, 255, 0, 0.15),
    0 0 15px rgba(0, 255, 0, 0.15),
    0 0 20px rgba(0, 255, 0, 0.15),
    0 0 25px rgba(0, 255, 0, 0.15),
    0 0 30px rgba(0, 255, 0, 0.15);
}
