cardCss:
@import url("https://fonts.googleapis.com/css2?family=Roboto:ital,wght@1,700&display=swap");

:root {
}

.roboto-bold-italic {
  font-family: "Roboto", sans-serif;
  font-weight: 700;
  font-style: italic;
  letter-spacing: 0.015rem;
}

.fot-rodin-m {
  font-family: "Noto Sans JP", sans-serif;
}

.fot-rodin-ub {
  font-family: "Noto Sans JP", sans-serif;
}

/*.card is primarily for styling the background of both front and back cards*/
.card {
  background-color: black;
}

.ts-4 {
  text-shadow:
    0px 0px 4px rgba(0, 0, 0, 0.2),
    2px 2px 4px rgba(0, 0, 0, 0.2),
    2px 2px 6px rgba(0, 0, 0, 0.2);
}

.image-container {
  position: relative;
  height: 100%;
  overflow: hidden;
  background-color: black;
}

.bg-image {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  height: 100%;
  object-fit: cover;

  z-index: 0; /* Under the main image */
  opacity: 1;
}

.bg-image img {
  height: 100%;
  object-fit: cover;
  vertical-align: middle;
  filter: brightness(0.8);
}

.balance {
  text-wrap: balance;
}

.bg-black-40 {
  background: rgba(0, 0, 0, 0.4);
}

.br5 {
  border-radius: 5rem;
}

.text-box {
  min-height: 8rem;
}

.darken {
  filter: brightness(0.9);
}

.kern-jpn {
  letter-spacing: -0.07rem;
}

.italic-japanese {
  transform: skewX(-8deg);
}

.bg-off-white {
  background-color: #fffde4;
}

.off-white {
  color: #fffde4;
}

.f0 {
  font-size: 0;
}
