cardCss:
@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+JP:wght@100..900&display=swap');

@import url('https://fonts.googleapis.com/css2?family=Noto+Serif+JP:wght@200..900&display=swap');

:root {

}

.noto-serif-jp {
   font-family: "Noto Serif JP", serif;
}

.noto-sans-jp {
   font-family: "Noto Sans JP", sans-serif;
}

/*.card is primarily for styling the background of both front and back cards*/
.card {
   background-color: black;
}

.ts-4 {
    text-shadow: 2px 2px 8px rgba(0,0,0,.2);
}

.image-container {
  position: relative;
  height: 100%;
  width: 100%;
  overflow: hidden;
}

.image-container::before {
  content: "";
  position: absolute;
  top: -2em;
  left: -2em;
  right: -2em;
  bottom: -2em;
  background-image: var(--image-url);
  background-size: cover;
  background-position: center;
  filter: blur(15px); /* Apply a blur effect */
  z-index: 0; /* Under the main image */
  opacity: 50%;
}

img {
  position: absolute;
  top: 50%; /* Center vertically */
  left: 0;
  right: 0;
  width: 100%; /* Full container width */
  height: auto; /* Maintain aspect ratio */
  transform: translateY(-50%); /* Center the image vertically */
  z-index: 1; /* Ensures the image is above the pseudo-element */
}

.balance {
   text-wrap: balance;
}
