cardCss:
@import url('https://fonts.googleapis.com/css2?family=Arimo:wght@400..700&display=swap');

@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+JP:wght@100..900&display=swap');

:root {
    --neon-blue: cyan;
}

.arimo {
  font-family: "Arimo", sans-serif;
  font-optical-sizing: auto;
  font-style: normal;
}

/*.card is primarily for styling the background of both front and back cards*/
.card {
   background-color: black;
   -ms-overflow-style: none;  /* IE and Edge */
   scrollbar-width: none;  /* Firefox */
   overflow: hidden;
   overflow-y: scroll;
}

.noto-sans-jp {
  font-family: "Noto Sans JP", sans-serif;
  font-optical-sizing: auto;
  font-style: normal;
}

.matrix {
    position: relative;
    display: inline-block;
}

.matrix::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 255, 255, 0.1); /* Orange color with some opacity */
    mix-blend-mode: overlay;
}

.matrix:hover::after {
    background: none;  /* Remove the background on hover */
    mix-blend-mode: normal; /* Reset mix-blend-mode */
}

.image-overlay img {
    position: absolute;
    top: 0;
    left: 0;
    bottom: 0;
    right: 0;
    width: 100%;      /* Ensures overlay size matches the base image */
    height: auto;
    display: block;   /* Removes any extra space below the image */
    filter: saturate(0.5) contrast(1.25);
}

.image-overlay:hover img {
    filter: saturate(1) contrast(1);
}

.image-overlay img {
    position: relative;  /* Keeps the base image in the normal flow */
}

.glowing-text {
  color: white; /* White text */
  text-shadow: 
    2px 2px 2px rgba(0, 0, 0, 1);
}

.content-wrapper {

}

.border-glow {
  box-shadow: 
        0 0 2px rgba(255, 255, 255, 1),
        0 0 4px rgba(200, 200, 200, 1),
        0 0 6px rgba(100, 100, 100, 1),
        0 0 8px rgba(0, 0, 0, 1),
        0 0 2px rgba(255, 255, 255, 1),
        0 0 4px rgba(200, 200, 200, 1),
        0 0 6px rgba(100, 100, 100, 1),
        0 0 8px rgba(0, 0, 0, 1),
        0 0 2px rgba(255, 255, 255, 1),
        0 0 4px rgba(200, 200, 200, 1),
        0 0 6px rgba(100, 100, 100, 1),
        0 0 8px rgba(0, 0, 0, 1),
        0 0 2px rgba(255, 255, 255, 1),
        0 0 4px rgba(200, 200, 200, 1),
        0 0 6px rgba(100, 100, 100, 1),
        0 0 8px rgba(0, 0, 0, 1),
        inset 0 0 2px rgba(255, 255, 255, 1),
        inset 0 0 4px rgba(200, 200, 255, 1),
        inset 0 0 6px rgba(100, 100, 100, 1),
        inset 0 0 8px rgba(0, 0, 0, 1),
        inset 0 0 2px rgba(255, 255, 255, 1),
        inset 0 0 4px rgba(200, 200, 255, 1),
        inset 0 0 6px rgba(100, 100, 100, 1),
        inset 0 0 8px rgba(0, 0, 0, 1),
        inset 0 0 2px rgba(255, 255, 255, 1),
        inset 0 0 4px rgba(200, 200, 255, 1),
        inset 0 0 6px rgba(100, 100, 100, 1),
        inset 0 0 8px rgba(0, 0, 0, 1),
        inset 0 0 2px rgba(255, 255, 255, 1),
        inset 0 0 4px rgba(200, 200, 255, 1),
        inset 0 0 6px rgba(100, 100, 100, 1),
        inset 0 0 8px rgba(0, 0, 0, 1);
}

.lh-0, .lh-0 img {
   line-height: 0;
   vertical-align: middle;
}

.focused-text:focus::before, .play-button:focus::before {
    content: '';
    background-image: url('pointer.svg');
    display: inline-block;
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
    width: 40px;
    height: 20px;
    left: -0.25rem;
    vertical-align: middle;
    transform: translate(-100%, -50%);
    position: absolute;
    top: 50%; /* Center vertically relative to the focused element */
    z-index: 9999;
}

.focused-text {
   position: relative;
}

.overflow-x-visible {
   overflow-x: unset;
}
