.lh-0,
.lh-0 img {
  line-height: 0;
  vertical-align: middle;
}

.focused-text:focus::before,
.play-button:focus::before {
  content: "";
  background-image: url("pointer.svg");
  display: inline-block;
  background-size: contain;
  background-repeat: no-repeat;
  background-position: center;
  width: 40px;
  height: 20px;
  left: -0.25rem;
  vertical-align: middle;
  transform: translate(-100%, -50%);
  position: absolute;
  top: 50%; /* Center vertically relative to the focused element */
  z-index: 9999;
}

.focused-text {
  position: relative;
}

.focused-text:focus,
.focused-text:active {
  /*outline: none;
  border: none;*/
}

.overflow-x-visible {
  overflow-x: unset;
}

.bg-linear-blue {
  background: linear-gradient(153.84deg, #0100ac 0%, #000022 100%);
}

.play-button {
  color: inherit;
  background-color: transparent;
  border: none;
  z-index: inherit;
  cursor: inherit;
  text-shadow: inherit;
  line-height: inherit;
  padding: 0;
}

.lh-audio {
  line-height: 2.5rem;
}

.grow-custom > * {
  -moz-osx-font-smoothing: grayscale;
  backface-visibility: hidden;
  transform: translateZ(0);
  transition: transform 0.25s ease-out;
}

.grow-custom > *:hover,
.grow-custom > *:focus {
  transform: scale(1.05);
}

.grow-custom > *:active {
  transform: scale(0.9);
}

