cardCss:
@import url("https://unpkg.com/tachyons@4.12.0/css/tachyons.min.css");

@import url("https://fonts.googleapis.com/css2?family=Chilanka&display=swap");

@import url("https://fonts.googleapis.com/css2?family=Yomogi&display=swap");

:root {
  --paper-bg-color: #fff;
  --line-color: #9198e5;
  --margin-line-color: pink;
  --dark-gray: #333333;
}

.line-blue {
  color: var(--line-color);
}

.line-red {
  color: var(--margin-line-color);
}

/* Anki-only styling to take up all vertical space of the card display window */
html,
body,
div#qa {
  height: 100%;
}

.vertical-align-custom {
  vertical-align: baseline;
}

.yomogi-regular {
  font-family: "Yomogi", cursive;
  font-weight: 400;
  font-style: normal;
}

.card {
  position: relative;
  font-family: Chilanka, cursive;
  background-color: white;
}

.lined-bg {
  background: repeating-linear-gradient(
    white,
    white 2.1rem,
    var(--line-color) 2.175rem,
    var(--line-color) 2.275rem
  );
}

.card header {
  background: linear-gradient(
    white,
    white 4.55rem,
    var(--margin-line-color) 4.575rem,
    var(--margin-line-color) 4.6rem
  );
  height: 4.7rem;
}

.sticky {
  position: sticky;
}

.pencil-effect {
  filter: brightness(2) grayscale(1);
  mix-blend-mode: normal;
  position: relative;
  opacity: 100%;
}

.pencil-effect::before {
  content: "";
  display: block;
  position: absolute;
  background-image: url(sketch_lines.jpg);
  background-size: cover;
  left: 0;
  top: 0;
  width: 100%;
  height: 100%;
  mix-blend-mode: overlay;
}

.pencil-effect:hover {
  filter: brightness(1) grayscale(0);
  mix-blend-mode: normal;
  opacity: 100%;
}

.pencil-effect:hover::before {
  opacity: 0;
}

.pencil-effect img {
  display: block;
  width: 100%;
}

.italic-japanese {
  transform: skewX(-8deg);
}

.lh-0 {
  line-height: 0;
}

.lh-audio {
  line-height: 2.5rem;
}

.grow-custom > * {
  -moz-osx-font-smoothing: grayscale;
  backface-visibility: hidden;
  transform: translateZ(0);
  transition: transform 0.25s ease-out;
}

.grow-custom > *:hover,
.grow-custom > *:focus {
  transform: scale(1.05);
}

.grow-custom > *:active {
  transform: scale(0.9);
}

/* input-reset input-8-bit f3 w-100 dark-green ba b--medium-green bg-green pa2 */
#input-container input {
  -webkit-appearance: none;
  background-color: transparent;
  color: var(--dark-gray);
  font-size: 1.5rem;
  padding: 0.5rem;
  border: 1px solid transparent;
  width: 100%;
  -webkit-appearance: none;
  -moz-appearance: none;
}

#input-container input::placeholder {
  color: var(--dark-gray);
  opacity: 0.6;
}

#input-container input:focus {
  outline: 0 none;
  background-color: transparent;
  border: 1px solid transparent !important;
}

#input-container input::-moz-focus-inner {
  border: 0;
  padding: 0;
}

