  content: "â–¶";
  color: var(--dark-gray);
}

.append-play-btn-text svg {
  display: none;
}

.append-play-btn-text button {
  font-size: 1.5rem;
  cursor: pointer;
}

.append-play-btn-text {
}

.replay-button {
  margin: 0;
  font-size: 1.5rem;
}

.play-button {
