cardCss:
@import url("https://fonts.googleapis.com/css2?family=Source+Code+Pro:wght@200..900&display=swap");

@import url("https://fonts.googleapis.com/css2?family=M+PLUS+1+Code:wght@100..700&display=swap");

:root {
  --zb-bg-color: #3f3f3f;
  --zb-default-color: #dcdccc;
  --zb-bright-yellow: #efef8f;
  --zb-cyan: #8cd0d3;
  --zb-muted-green: #7f9f7f;
  --zb-salmon: #dca3a3;
}

/*.card is primarily for styling the background of both front and back cards*/
.card {
  background-color: var(--zb-bg-color);
}

.m-plus-1-code {
  font-family: "M PLUS 1 Code", monospace;
  font-optical-sizing: auto;
  font-style: normal;
}

.source-code-pro {
  font-family: "Source Code Pro", monospace;
}

.zb-default {
  color: var(--zb-default-color);
}

.zb-bright-yellow {
  color: var(--zb-bright-yellow);
}

.zb-cyan {
  color: var(--zb-cyan);
}

.zb-muted-green {
  color: var(--zb-muted-green);
}

.zb-salmon {
  color: var(--zb-salmon);
}

.bright {
  opacity: 0.5;
  transition: opacity 0.15s ease-in;
}
.bright:hover,
.bright:focus {
  opacity: 1;
  transition: opacity 0.15s ease-in;
}

.image-wrapper img {
  display: block;
}

.play-button {
  color: inherit;
  background-color: transparent;
  border: none;
  z-index: inherit;
  cursor: inherit;
  text-shadow: inherit;
  line-height: inherit;
  padding: 0;
}

.lh-audio {
  line-height: 2.5rem;
}

.grow-custom > * {
  -moz-osx-font-smoothing: grayscale;
  backface-visibility: hidden;
  transform: translateZ(0);
  transition: transform 0.25s ease-out;
}

.grow-custom > *:hover,
.grow-custom > *:focus {
  transform: scale(1.05);
}

.grow-custom > *:active {
  transform: scale(0.9);
}

/* input-reset input-8-bit f3 w-100 dark-green ba b--medium-green bg-green pa2 */
#input-container input {
  -webkit-appearance: none;
  background-color: transparent;
  color: var(--zb-default-color);
  font-size: 1.5rem;
  padding: 0.5rem;
  border: 1px solid gray;
  width: 100%;
  text-align: left;
  -webkit-appearance: none;
  -moz-appearance: none;
}

#input-container input::placeholder {
  color: var(--zb-default-color);
  opacity: 0.7;
}

#input-container input:focus {
  outline: 0 none;
  background-color: transparent;
  border: 1px solid var(--zb-default-color) !important;
}

#input-container input::-moz-focus-inner {
  border: 0;
  padding: 0;
}

