cardCss:
@import url("https://unpkg.com/tachyons@4.12.0/css/tachyons.min.css");

@import url("https://fonts.googleapis.com/css2?family=M+PLUS+Rounded+1c:wght@700&display=swap");

:root {
  --chalk-bg-color: #3b383f;
  --default-chalk: #ededed;
  --chalk-yellow: #fff58a;
  --chalk-light-blue: #a1c9f0;
  --chalk-light-green: #a8f0d6;
  --chalk-pink: #ffabc4;
}

.m-plus-rounded-1c-bold {
  font-family: "M PLUS Rounded 1c", sans-serif;
  font-weight: 700;
  font-style: normal;
}

/* https://font.download/font/chalkduster */
@font-face {
  font-family: Chalkduster;
  src:
    url(Chalkduster.woff) format("woff"),
    url("https://cdn.jsdelivr.net/gh/avidrucker/anki-card-test-1/public/Chalkduster.woff")
      format("woff");
}

.chalkduster {
  font-family: Chalkduster, cursive;
}

/*.card is primarily for styling the background of both front and back cards*/
.card {
  background-color: var(--chalk-bg-color);
  background-image: linear-gradient(rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0.5)),
    url(bg_blackboard_l_opt.jpg),
    url("https://cdn.jsdelivr.net/gh/avidrucker/anki-card-test-1/public/bg_blackboard_l_opt.jpg");
  background-position: center;
  background-size: cover;
}

.default-chalk {
  color: var(--default-chalk);
}

.chalk-yellow {
  color: var(--chalk-yellow);
}

.chalk-light-blue {
  color: var(--chalk-light-blue);
}

.chalk-light-green {
  color: var(--chalk-light-green);
}

.chalk-pink {
  color: var(--chalk-pink);
}

.img-wrapper {
  position: relative;
  display: inline-block;
  height: auto;
  mix-blend-mode: lighten;
  filter: saturate(1.25);
  border-radius: 15px;
}

.img-wrapper img {
  display: block;
  width: 100%;
  height: auto;
  border-radius: 1rem;
  line-height: 0;
}

.img-wrapper::before,
.img-wrapper::after {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  border-radius: 15px;
}

.img-wrapper::after {
  background-color: white;
  opacity: 5%;
}

.img-wrapper:hover::after {
  opacity: 0;
}

.img-wrapper:hover::before {
  opacity: 1;
  mix-blend-mode: normal;
}

.img-wrapper:hover {
  mix-blend-mode: normal;
  filter: saturate(1);
}

.chalk-filter {
  filter: url(#chalkEffect);
}

.top-wrapper img {
  border-radius: 15px;
  line-height: 0;
}

.top-wrapper {
  opacity: 0;
  transition: opacity 0.5s ease-in-out;
}

.top-wrapper:hover {
  opacity: 1;
}

.v-align-mid,
.v-align-mid img {
  vertical-align: middle;
  line-height: 0;
}

/* Anki-only styling to take up all vertical space of the card display window */
html,
body,
div#qa {
  height: 100%;
}

/* Hide scrollbar for Chrome, Safari and Opera */
.invisible-scrollbar::-webkit-scrollbar {
  display: none;
}

/* Hide scrollbar for IE, Edge and Firefox */
.invisible-scrollbar {
  -ms-overflow-style: none; /* IE and Edge */
  scrollbar-width: none; /* Firefox */
}

.visually-hidden {
  position: absolute;
  width: 0px;
  height: 0px;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  white-space: nowrap; /* Prevents content from "spilling" */
  border-width: 0;
}

.visually-hidden:hover {
  position: inherit;
  width: auto;
  height: auto;
  margin: 0;
  overflow: visible;
  clip: auto;
  white-space: normal;
  border-width: 0;
}

.off-screen {
  position: absolute;
  left: -9999px;
}

.balance {
  text-wrap: balance;
}

.play-button {
  color: inherit;
  background-color: transparent;
  border: none;
  z-index: inherit;
  cursor: inherit;
  text-shadow: inherit;
  line-height: inherit;
  padding: 0;
}

.lh-audio {
  line-height: 2.5rem;
}

.grow-custom > * {
  -moz-osx-font-smoothing: grayscale;
  backface-visibility: hidden;
  transform: translateZ(0);
  transition: transform 0.25s ease-out;
}

.grow-custom > *:hover,
.grow-custom > *:focus {
  transform: scale(1.05);
}

.grow-custom > *:active {
  transform: scale(0.9);
}

/* input-reset input-8-bit f3 w-100 dark-green ba b--medium-green bg-green pa2 */
#input-container input {
  -webkit-appearance: none;
  background-color: transparent;
  color: var(--default-chalk);
  font-size: 1.5rem;
  padding: 0.5rem;
  border: 2px solid var(--default-chalk);
  border-width: 2px;
  text-align: center;
  -webkit-appearance: none;
  -moz-appearance: none;
}

#input-container input::placeholder {
  color: var(--default-chalk);
  opacity: 0.6;
}

#input-container input:focus {
  outline: 0 none;
  background-color: transparent;
  border: 2px solid var(--default-chalk) !important;
}

#input-container input::-moz-focus-inner {
  border: 0;
  padding: 0;
}

/* Anki Only Styles*/
.append-play-btn-text a::after {
  content: "â–¶";
  color: var(--default-chalk);
}

.append-play-btn-text svg {
  display: none;
}

.append-play-btn-text button {
  font-size: 2.5rem;
  height: 3rem;
  width: 3rem;
  cursor: pointer;
}

.append-play-btn-text {
  height: 3rem;
  line-height: 3rem;
}

.replay-button {
  margin: 0;
  font-size: 2.5rem;
}
