cardCss:
@import url("https://fonts.googleapis.com/css2?family=M+PLUS+Rounded+1c:wght@700&display=swap");

:root {
  --chalk-bg-color: #3b383f;
  --default-chalk: #ededed;
  --chalk-yellow: #fff58a;
  --chalk-light-blue: #a1c9f0;
  --chalk-light-green: #a8f0d6;
  --chalk-pink: #ffabc4;
}

.m-plus-rounded-1c-bold {
  font-family: "M PLUS Rounded 1c", sans-serif;
  font-weight: 700;
  font-style: normal;
}

/* https://font.download/font/chalkduster */
@font-face {
  font-family: Chalkduster;
  src: url(Chalkduster.woff) format("woff");
}

.chalkduster {
  font-family: Chalkduster, cursive;
}

/*.card is primarily for styling the background of both front and back cards*/
.card {
  background-color: var(--chalk-bg-color);
  background-image: linear-gradient(rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0.5)),
    url(bg_blackboard_l_opt.jpg);
  background-position: center;
  background-size: cover;
}

.default-chalk {
  color: var(--default-chalk);
}

.chalk-yellow {
  color: var(--chalk-yellow);
}

.chalk-light-blue {
  color: var(--chalk-light-blue);
}

.chalk-light-green {
  color: var(--chalk-light-green);
}

.chalk-pink {
  color: var(--chalk-pink);
}

.img-wrapper {
  position: relative;
  display: inline-block;
  height: auto;
  mix-blend-mode: lighten;
  filter: saturate(1.25);
  border-radius: 15px;
}

.img-wrapper img {
  display: block;
  width: 100%;
  height: auto;
  border-radius: 1rem;
  line-height: 0;
}

.img-wrapper::before,
.img-wrapper::after {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  border-radius: 15px;
}

.img-wrapper::after {
  background-color: white;
  opacity: 5%;
}

.img-wrapper:hover::after {
  opacity: 0;
}

.img-wrapper:hover::before {
  opacity: 1;
  mix-blend-mode: normal;
}

.img-wrapper:hover {
  mix-blend-mode: normal;
  filter: saturate(1);
}

.chalk-filter {
  filter: url(#chalkEffect);
}

.top-wrapper img {
  border-radius: 15px;
  line-height: 0;
}

.top-wrapper {
  opacity: 0;
  transition: opacity 0.5s ease-in-out;
}

.top-wrapper:hover {
  opacity: 1;
}
