cardCss:
@import url('https://fonts.googleapis.com/css2?family=M+PLUS+Rounded+1c:wght@700&display=swap');

:root {
    --chalk-bg-color: #3b383f;
    --default-chalk: #ededed;
    --chalk-yellow: #fff58a;
    --chalk-light-blue: #a1c9f0;
    --chalk-light-green: #a8f0d6;
    --chalk-pink: #ffabc4;
}

.m-plus-rounded-1c-bold {
  font-family: "M PLUS Rounded 1c", sans-serif;
  font-weight: 700;
  font-style: normal;
}

/* https://font.download/font/chalkduster */
@font-face {
    font-family: Chalkduster;
    src: url(Chalkduster.woff) format('woff'); 
}

.chalkduster {
   font-family: Chalkduster, cursive;
}

/*.card is primarily for styling the background of both front and back cards*/
.card {
   background-color: var(--chalk-bg-color);
   background-image: url(bg_blackboard_l_opt.jpg);
   background-position: center;
   background-size: cover;

}

.default-chalk {
   color: var(--default-chalk);
}

.chalk-yellow {
    color: var(--chalk-yellow);
}

.chalk-light-blue {
    color: var(--chalk-light-blue);
}

.chalk-light-green {
    color: var(--chalk-light-green);
}

.chalk-pink {
    color: var(--chalk-pink);
}

.img-wrapper {
    position: relative;
    display: inline-block;
    height: auto;
    mix-blend-mode: overlay;
    filter: saturate(1.25);
}

.img-wrapper img {
    display: block;
    width: 100%;
    height: auto;
    border-radius: 1rem;
}

.img-wrapper::before, .img-wrapper::after {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    border-radius: 15px;
}

.img-wrapper::before {
    background: var(--image-url) center/cover no-repeat;
    opacity: 0.5;
    mix-blend-mode: screen;
    filter: brightness(0) contrast(0.5);
}

.img-wrapper::after {
    background-color: white;
    opacity: 15%;
}

.img-wrapper:hover::after {
    opacity: 0;
}

.img-wrapper:hover::before {
    opacity: 1;
    mix-blend-mode: normal;
    filter: brightness(1) contrast(1);
}

.img-wrapper:hover {
    mix-blend-mode: normal;
    filter: saturate(1);
}
