cardCss:
@import url("https://fonts.googleapis.com/css2?family=DotGothic16&display=swap");

.dotgothic16-regular {
  font-family: "DotGothic16", sans-serif;
  font-weight: 400;
  font-style: normal;
}

/* https://fontlibrary.org/en/font/chicagoflf */
@font-face {
  font-family: ChicagoFLF;
  src: url(ChicagoFLF.ttf) format("truetype");
}

.chicago-flf {
  font-family: "ChicagoFLF", sans-serif;
}

:root {
}

.unifont-medium {
  font-family: "UnifontMedium", mono;
  font-weight: normal;
  font-style: normal;
}

/*.card is primarily for styling the background of both front and back cards*/
.card {
  overflow-y: hidden;
}

.dark-green {
  color: var(--dark-green);
}

.medium-green {
  color: var(--medium-green);
}

.bg-green {
  background-color: var(--green);
}

.bg-light-gray {
  background-color: var(--light-gray);
}

.green-filter {
  filter: url(#colorFilter);
}

.green-filter:hover {
  filter: url(); /* intentionally left blank */
}

.image-wrapper img {
  display: block;
  height: auto;
}

.image-wrapper {
  display: inline-block;
  height: auto;
}

.shift-up-left {
  transform: translate(-0.5rem, -0.25rem);
}

.lh-audio {
  height: 1rem;
}

.bg-checkered {
  background-image: url(Swatch.svg);
  background-repeat: repeat;
}

.dither-5 {
  /* Apply the SVG filter */
  filter: url(#dithering-filter5);
}

.show-on-hover {
  transition: opacity 0.5s ease-in-out;
}
.show-on-hover:hover {
  opacity: 1;
}

.macmouse {
  cursor: url(macmouse.png), auto;
}

.macmouse .macpointer {
  cursor: url("macmouse_pointer.png"), auto;
}
