cardCss:
@import url("https://fonts.googleapis.com/css2?family=Courier+Prime:ital,wght@0,400;0,700;1,400;1,700&display=swap");

@import url("https://fonts.googleapis.com/css2?family=M+PLUS+1+Code:wght@100..700&display=swap");

:root {
  --matrix-green: lime;
}

.courier-prime {
  font-family: "Courier Prime", mono;
}

/*.card is primarily for styling the background of both front and back cards*/
.card {
  background-color: black;
}

.m-plus-1-code {
  font-family: "M PLUS 1 Code", monospace;
  font-optical-sizing: auto;
  font-style: normal;
}

.matrix-green {
  color: var(--matrix-green);
}

.matrix {
  position: relative;
  display: inline-block;
}

.matrix::after {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 150, 0, 0.25); /* Orange color with some opacity */
  mix-blend-mode: overlay;
}

.matrix:hover::after {
  background: none; /* Remove the background on hover */
  mix-blend-mode: normal; /* Reset mix-blend-mode */
}

.image-overlay {
  position: relative;
  display: inline-block;
  overflow: hidden;
}

.image-overlay img {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%; /* Ensures overlay size matches the base image */
  height: auto;
  display: block; /* Removes any extra space below the image */
}

.image-overlay img:first-child {
  position: relative; /* Keeps the base image in the normal flow */
}

.dimmed {
  filter: brightness(0.15);
}

.img-filter {
  mix-blend-mode: exclusion;
  filter: brightness(0.8);
}

.img-filter:hover {
  mix-blend-mode: normal;
  filter: brightness(1);
}

.lh-0 {
  line-height: 0;
  vertical-align: middle;
}

.glowing-text {
  color: white; /* White text */
  text-shadow:
    0 0 5px rgba(0, 255, 0, 0.15),
    0 0 10px rgba(0, 255, 0, 0.15),
    0 0 15px rgba(0, 255, 0, 0.15),
    0 0 20px rgba(0, 255, 0, 0.15),
    0 0 25px rgba(0, 255, 0, 0.15),
    0 0 30px rgba(0, 255, 0, 0.15),
    0 0 5px rgba(0, 255, 0, 0.15),
    0 0 10px rgba(0, 255, 0, 0.15),
    0 0 15px rgba(0, 255, 0, 0.15),
    0 0 20px rgba(0, 255, 0, 0.15),
    0 0 25px rgba(0, 255, 0, 0.15),
    0 0 30px rgba(0, 255, 0, 0.15);
}
