:root {

}

.klee-one-regular {
  font-family: "Klee One", cursive;
  font-weight: 400;
  font-style: normal;
}

.klee-one-semibold {
  font-family: "Klee One", cursive;
  font-weight: 600;
  font-style: normal;
}

.eb-garamond {
  font-family: "EB Garamond", serif;
  font-optical-sizing: auto;
  font-style: normal;
}

.brown {
  color: #7d3b1d;
}

.bg-brown {
  background-color: #7d3b1d;
}


/*.card is primarily for styling the background of both front and back cards*/
.card {
   background-image: url(bg_parchment_l_opt.jpg);
   background-position: center;
   background-size: cover;

}

.pencil-effect {
    filter: brightness(2) grayscale(1) sepia(1);
    mix-blend-mode: normal;
    border-radius: 15px;
    opacity: 100%;
}

.pencil-effect::before {
    content: "";
    display: block;
    position: absolute;
    background-image: url(sketch_lines.jpg);
    background-size: cover;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    mix-blend-mode: overlay;
    border-radius: 15px;
}

.pencil-effect:hover {
    filter: none;
    mix-blend-mode: normal;
    opacity: 1;
}

.pencil-effect:hover::before {
    opacity: 0;
}

.pencil-effect img {
    display: block;
    width: 100%;
    border-radius: 15px;
}

.img-wrapper {
    position: relative;
    display: inline-block;
    height: auto;
    mix-blend-mode: multiply;
    filter: saturate(1.25);
    border-radius: 15px;
}

.img-wrapper img {
    border-radius: 1rem;
    display: block;
}

.img-wrapper::before, .img-wrapper::after {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    border-radius: 15px;
}

.img-wrapper:hover {
    mix-blend-mode: normal;
    filter: saturate(1);
}

.multiply {
    mix-blend-mode: multiply;
}

.img-height-fix {
    line-height: 0;
}

.multiply:hover {
    mix-blend-mode: normal;
    opacity: 1;
}
