/*.card is primarily for styling the background of both front and back cards*/
.card {
   background-color: black;
}

.ts-4 {
    text-shadow: 0px 0px 10px rgba(0,0,0,.25);
}

.image-container {
  position: relative;
  height: 100%;
  overflow: hidden;
  background-color: black;
}

.bg-image {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  height: 100%;
  object-fit: cover;

  z-index: 0; /* Under the main image */
  opacity: 1;
}

.bg-image img {
  height: 100%;
  object-fit: cover;
  vertical-align: middle;
}

.display-image {
  position: absolute;
  left: 0;
  top: 0;
  width: 100%;
  height: 100%;
  z-index: 1;
  display: flex;
  justify-content: center;
  align-items: center;
}

.display-image img {
  width: 100%;
  height: auto;
}

.balance {
   text-wrap: balance;
}

.bg-black-40 {
   background: rgba(0, 0, 0, 0.4);
}

.br5 {
   border-radius: 5rem;
}

.text-box {
   min-height: 8rem;
}

.darken {
   filter: brightness(0.9);
}

.kern-jpn {
   letter-spacing: -0.07rem;
}

.italic-japanese {
    transform: skewX(-8deg);
}
