cardCss:
@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+JP:wght@100..900&display=swap');

@import url('https://fonts.googleapis.com/css2?family=Noto+Serif+JP:wght@200..900&display=swap');

:root {

}

.noto-serif-jp {
   font-family: "Noto Serif JP", serif;
}

.noto-sans-jp {
   font-family: "Noto Sans JP", sans-serif;
}

/*.card is primarily for styling the background of both front and back cards*/
.card {
   background-color: black;
}

.ts-4 {
    text-shadow: 2px 2px 8px rgba(0,0,0,.2);
}

.image-container {
  position: relative;
  height: 100%;
  overflow: hidden;
  background-color: black;
}

.bg-image {
  position: absolute;
  top: -25%;
  left: -25%;
  height: 150%;
  object-fit: cover;
  filter: blur(15px); /* Apply a blur effect */
  z-index: 0; /* Under the main image */
  opacity: 25%;
}

.bg-image img {
  height: 100%;
  object-fit: cover;
}

.display-image {
  position: absolute;
  left: 0;
  top: 0;
  width: 100%;
  height: 100%;
  z-index: 1;
  display: flex;
  justify-content: center;
  align-items: center;
}

.display-image img {
  width: 100%;
  height: auto;
}

.balance {
   text-wrap: balance;
}
