cardCss:
@import url("https://fonts.googleapis.com/css2?family=Exo+2:wght@100..900&display=swap");

@import url("https://fonts.googleapis.com/css2?family=Noto+Sans+JP:wght@100..900&display=swap");

:root {
  --neon-blue: cyan;
}

.exo-2 {
  font-family: "Exo 2", sans-serif;
  font-optical-sizing: auto;
  font-style: normal;
}

/*.card is primarily for styling the background of both front and back cards*/
.card {
  background-color: black;
  -ms-overflow-style: none; /* IE and Edge */
  scrollbar-width: none; /* Firefox */
  overflow: hidden;
}

.noto-sans-jp {
  font-family: "Noto Sans JP", sans-serif;
  font-optical-sizing: auto;
  font-style: normal;
}

.matrix {
  position: relative;
  display: inline-block;
}

.matrix::after {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 255, 255, 0.25); /* Orange color with some opacity */
  mix-blend-mode: overlay;
}

.matrix:hover::after {
  background: none; /* Remove the background on hover */
  mix-blend-mode: normal; /* Reset mix-blend-mode */
}

.image-overlay img {
  position: absolute;
  top: 0;
  left: 0;
  bottom: 0;
  right: 0;
  width: 100%; /* Ensures overlay size matches the base image */
  height: auto;
  display: block; /* Removes any extra space below the image */
  filter: saturate(0.5) contrast(1.25);
}

.image-overlay:hover img {
  filter: saturate(1) contrast(1);
}

.image-overlay img {
  position: relative; /* Keeps the base image in the normal flow */
}

.glowing-text {
  color: white; /* White text */
  text-shadow:
    0 0 5px rgba(0, 255, 255, 0.15),
    /* Small cyan glow */ 0 0 10px rgba(0, 255, 255, 0.15),
    /* Medium cyan glow */ 0 0 15px rgba(0, 255, 255, 0.15),
    /* Larger cyan glow */ 0 0 20px rgba(0, 255, 255, 0.15),
    /* Even larger cyan glow */ 0 0 25px rgba(0, 255, 255, 0.15),
    /* Further larger cyan glow */ 0 0 30px rgba(0, 255, 255, 0.15),
    /* Largest cyan glow */ 0 0 5px rgba(0, 255, 255, 0.15),
    /* Small cyan glow */ 0 0 10px rgba(0, 255, 255, 0.15),
    /* Medium cyan glow */ 0 0 15px rgba(0, 255, 255, 0.15),
    /* Larger cyan glow */ 0 0 20px rgba(0, 255, 255, 0.15),
    /* Even larger cyan glow */ 0 0 25px rgba(0, 255, 255, 0.15),
    /* Further larger cyan glow */ 0 0 30px rgba(0, 255, 255, 0.15);
}

.background-image {
  position: absolute;
  top: 50%;
  left: 50%;

  min-height: 100vh;
  min-width: 100%;
  height: 100%;
  width: auto;
  z-index: 0;
  opacity: 0.2;
  top: 0;
  left: 0;
  bottom: 0;
  right: 0;
  object-fit: cover;
}

.content-wrapper {
  background: rgba(0, 0, 0, 0.1);
  box-shadow: 0 8px 32px 0 rgba(31, 38, 135, 0.37);
  backdrop-filter: blur(5px);
  -webkit-backdrop-filter: blur(5px);
  border-radius: 1.5rem;
}

.bloom img {
  filter: blur(10px) brightness(1.25);
  opacity: 0.5;
}

.bloom img:hover {
  filter: blur(0) brightness(1);
  opacity: 1;
}

.border-glow {
  box-shadow:
    0 0 5px rgba(0, 255, 255, 0.15),
    /* Small cyan glow */ 0 0 10px rgba(0, 255, 255, 0.15),
    /* Medium cyan glow */ 0 0 15px rgba(0, 255, 255, 0.15),
    /* Larger cyan glow */ 0 0 20px rgba(0, 255, 255, 0.15),
    /* Even larger cyan glow */ 0 0 25px rgba(0, 255, 255, 0.15),
    /* Further larger cyan glow */ 0 0 30px rgba(0, 255, 255, 0.15),
    /* Largest cyan glow */ inset 0 0 5px rgba(0, 255, 255, 0.15),
    /* Small inward cyan glow */ inset 0 0 10px rgba(0, 255, 255, 0.15),
    /* Medium inward cyan glow */ inset 0 0 15px rgba(0, 255, 255, 0.15),
    /* Larger inward cyan glow */ inset 0 0 20px rgba(0, 255, 255, 0.15),
    /* Even larger inward cyan glow */ inset 0 0 25px rgba(0, 255, 255, 0.15),
    /* Further larger inward cyan glow */ inset 0 0 30px rgba(0, 255, 255, 0.15); /* Largest inward cyan glow */
}

.lh-0,
.lh-0 img {
  line-height: 0;
  vertical-align: middle;
}

.play-button {
  color: inherit;
  background-color: transparent;
  border: none;
  z-index: inherit;
  cursor: inherit;
  text-shadow: inherit;
  line-height: inherit;
  padding: 0;
}

.lh-audio {
  line-height: 2.5rem;
}

.grow-custom > * {
  -moz-osx-font-smoothing: grayscale;
  backface-visibility: hidden;
  transform: translateZ(0);
  transition: transform 0.25s ease-out;
}

.grow-custom > *:hover,
.grow-custom > *:focus {
  transform: scale(1.05);
}

.grow-custom > *:active {
  transform: scale(0.9);
}

/* input-reset input-8-bit f3 w-100 dark-green ba b--medium-green bg-green pa2 */
#input-container input {
  -webkit-appearance: none;
  background-color: transparent;
  color: white;
  font-size: 1.5rem;
  padding: 0.5rem;
  border: 1px solid gray;
  width: 100%;
  text-align: left;
  -webkit-appearance: none;
  -moz-appearance: none;
}

#input-container input::placeholder {
  color: lightgray;
  opacity: 0.7;
}

#input-container input:focus {
  outline: 0 none;
  background-color: transparent;
  border: 1px solid lightgray !important;
}

#input-container input::-moz-focus-inner {
  border: 0;
  padding: 0;
}

/* Anki Only Styles*/
.append-play-btn-text a::after {
  content: "â–¶";
  color: white;
}

.append-play-btn-text svg {
  display: none;
}

.append-play-btn-text button {
  font-size: 2.5rem;
  line-height: 3rem;
  cursor: pointer;
  width: 3rem;
  padding-bottom: 0.5rem;
}

.append-play-btn-text {
  width: 3rem;
}

.replay-button {
  margin: 0;
  font-size: 2.5rem;
}
