cardCss:
@import url("https://fonts.googleapis.com/css2?family=Chilanka&display=swap");

@import url("https://fonts.googleapis.com/css2?family=Yomogi&display=swap");

:root {
  --paper-bg-color: #fff;
  --line-color: #007bff;
  --margin-line-color: #ff0000;
}

.vertical-align-custom {
  vertical-align: baseline;
}

.yomogi-regular {
  font-family: "Yomogi", cursive;
  font-weight: 400;
  font-style: normal;
}

.card {
  position: relative;
  font-family: Chilanka, cursive;
  background-color: white;
}

.lined-bg {
  background: repeating-linear-gradient(
    white,
    white 2.1rem,
    #9198e5 2.175rem,
    #9198e5 2.275rem
  );
}

.card header {
  background: linear-gradient(white, white 4.55rem, pink 4.575rem, pink 4.6rem);
  height: 4.7rem;
}

.sticky {
  position: sticky;
}

.pencil-effect {
  filter: brightness(2) grayscale(1);
  mix-blend-mode: normal;
  position: relative;
  opacity: 100%;
}

.pencil-effect::before {
  content: "";
  display: block;
  position: absolute;
  background-image: url(sketch_lines.jpg);
  background-size: cover;
  left: 0;
  top: 0;
  width: 100%;
  height: 100%;
  mix-blend-mode: overlay;
}

.pencil-effect:hover {
  filter: brightness(1) grayscale(0);
  mix-blend-mode: normal;
  opacity: 100%;
}

.pencil-effect:hover::before {
  opacity: 0;
}

.pencil-effect img {
  display: block;
  width: 100%;
}

.italic-japanese {
  transform: skewX(-8deg);
}
