cardCss:
@import url('https://fonts.googleapis.com/css2?family=Yuji+Syuku&display=swap');

:root {
    --rice-paper-bg-color: #fff2e8;
    --default-ink: black;
}

.yuji-syuku {
  font-family: "Yuji Syuku", cursive;
}

/*.card is primarily for styling the background of both front and back cards*/
.card {
   background-color: var(--rice-paper-bg-color);
   background-image: url(rice-paper-2.png);
}

.default-ink {
   color: var(--default-ink);
}

img {
  display: block;
  margin-left: auto;
  margin-right: auto;
}

.ink {
  filter: saturate(0%);
  mix-blend-mode: multiply;
}

.watercolor-effect {
    position: relative;
    overflow: hidden;
    background-color: red;
    width: 17rem;
    margin-left: auto;
    margin-right: auto;
  }

.watercolor-effect:before, .watercolor-effect:after {
    display: block;
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-repeat: no-repeat; /* Prevents tiling */
  }

.watercolor-effect:before {
    background-image: var(--image-url), var(--image-url);
    background-blend-mode: difference;
    background-position: calc(50% - 1px) calc(50% - 1px), calc(50% + 1px) calc(50% + 1px);
    filter: brightness(2.5) invert(1) grayscale(1);
    box-shadow: inset 0 0 0 1px black;
  }

.watercolor-effect:after {
    background-image: var(--image-url);
    background-position: center;
    mix-blend-mode: multiply;
    filter: brightness(1.5) blur(2px) contrast(1.5);
  }
