  background-image: linear-gradient(
      rgba(10, 10, 10, 0.5),
      rgba(10, 10, 10, 0.5)
    ),
    url("bg_starry_night.jpg"),
    url("https://cdn.jsdelivr.net/gh/avidrucker/anki-card-test-1/public/bg_starry_night.jpg");
