}

:root {
}

.text-stroke-yellow {
  filter: url(#text-stroke-yellow);
}

.lh-tiny-en {
  line-height: 0.85;
}

.lh-tiny-ja {
  line-height: 1.1;
}

.cyan {
  color: #00e5ff;
}

/*.card is primarily for styling the background of both front and back cards*/
.card {
  -ms-overflow-style: none; /* IE and Edge */
  scrollbar-width: none; /* Firefox */
}

.show-hide-children * {
  transition: opacity 0.5s ease-out;
}

.show-hide-children:hover *.show {
  opacity: 1;
}

.show-hide-children:hover *.hide {
  opacity: 0;
}

.show-on-hover {
  opacity: 0;
  transition: opacity 0.5s ease-out;
}

.show-on-hover:hover {
  opacity: 1;
}

.lh-0 {
  vertical-align: middle;
  line-height: 0;
}

/* Anki-only styling to take up all vertical space of the card display window */
html,
body,
div#qa {
  height: 100%;
}

/* Hide scrollbar for Chrome, Safari and Opera */
.invisible-scrollbar::-webkit-scrollbar {
  display: none;
}

.card::-webkit-scrollbar {
  display: none;
}

/* Hide scrollbar for IE, Edge and Firefox */
.invisible-scrollbar {
  -ms-overflow-style: none; /* IE and Edge */
  scrollbar-width: none; /* Firefox */
}

.shift-up-right {
  position: relative;
  top: -0.1rem;
}

.visually-hidden {
  position: absolute;
  width: 1px;
  height: 1px;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  white-space: nowrap; /* Prevents content from "spilling" */
  border-width: 0;
}

.visually-hidden:hover {
  position: inherit;
  width: auto;
  height: auto;
  margin: 0;
  overflow: visible;
  clip: auto;
  white-space: normal;
  border-width: 0;
}

.off-screen {
  position: absolute;
  left: -9999px;
}

.balance {
  text-wrap: balance;
}

.play-button {
  color: inherit;
  background-color: transparent;
  border: none;
  z-index: inherit;
  cursor: inherit;
  text-shadow: inherit;
  line-height: inherit;
  padding: 0;
}

.lh-audio {
  line-height: 2.5rem;
}

.grow-custom > * {
  -moz-osx-font-smoothing: grayscale;
  backface-visibility: hidden;
  transform: translateZ(0);
  transition: transform 0.25s ease-out;
}

.grow-custom > *:hover,
.grow-custom > *:focus {
  transform: scale(1.05);
}

.grow-custom > *:active {
  transform: scale(0.9);
}

/* input-reset input-8-bit f3 w-100 dark-green ba b--medium-green bg-green pa2 */
#input-container input {
  -webkit-appearance: none;
  background-color: transparent;
  color: white;
  font-size: 1.5rem;
  padding: 0.5rem;
  border: 1px solid gray;
  width: 100%;
  text-align: center;
  -webkit-appearance: none;
  -moz-appearance: none;
}

#input-container input::placeholder {
  color: lightgray;
  opacity: 0.7;
}

#input-container input:focus {
  outline: 0 none;
  background-color: transparent;
  border: 1px solid lightgray !important;
}

#input-container input::-moz-focus-inner {
  border: 0;
  padding: 0;
}

