cardCss:
@import url('https://fonts.googleapis.com/css2?family=DotGothic16&display=swap');

:root {
    --dark-green: #0F380F;
    --medium-green: #306230;
    --green: #8BAC0F;
    --light-green: #9BBC0F;
    --light-gray: #CBCBCB;
}

.dotgothic16 {
  font-family: "DotGothic16", sans-serif;
}


/*.card is primarily for styling the background of both front and back cards*/
.card {
   background-color: var(--light-gray);
   overflow-y: hidden;
}


.dark-green {
    color: var(--dark-green);
}

.medium-green {
    color: var(--medium-green);
}

.bg-green {
    background-color: var(--green);
}

.bg-light-gray {
    background-color: var(--light-gray);
}
