cardCss:
@import url('https://fonts.googleapis.com/css2?family=Courier+Prime:ital,wght@0,400;0,700;1,400;1,700&display=swap');

:root {
    --matrix-green: lime;
}

.courier-prime {
   font-family: "Courier Prime", mono;
}

/*.card is primarily for styling the background of both front and back cards*/
.card {
   background-color: black;
}


.matrix-green {
    color: var(--matrix-green);
}

.matrix {
    position: relative;
    display: inline-block;
}

.matrix::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 150, 0, 0.25); /* Orange color with some opacity */
    mix-blend-mode: overlay;
    pointer-events: none;
}

.image-overlay {
    position: relative;
    display: inline-block;
    overflow: hidden;
}

.image-overlay img {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;      /* Ensures overlay size matches the base image */
    height: auto;
    display: block;   /* Removes any extra space below the image */
}

.image-overlay img:first-child {
    position: relative;  /* Keeps the base image in the normal flow */
}

.image-overlay img:last-child {
    mix-blend-mode: hard-light;
    opacity: 0.25;
}
