cardCss:
@import url('https://fonts.googleapis.com/css2?family=Chilanka&display=swap');

:root {
    --paper-bg-color: #fff; // White background for paper
    --line-color: #007bff; // Blue line color
    --margin-line-color: #ff0000; // Red line color
}

.card {
    position: relative;
    font-family: Chilanka, cursive;
    background-color: white;
    background: repeating-linear-gradient(white, white 2.1rem, #9198e5 2.175rem, #9198e5 2.275rem);
}

.card header {
   background: linear-gradient(white, white 4.55rem, pink 4.575rem, pink 4.6rem);
   height: 4.7rem;
}
