cardCss:
:root {
    --rice-paper-bg-color: #fff2e8;
    --default-ink: black;
}

@font-face {
    font-family: Otsutome;
    src: url(/fonts/OtsutomeFont_Ver3_15.woff) format('woff'); 
}

.otsutome {
   font-family: Otsutome, cursive;
}

/*.card is primarily for styling the background of both front and back cards*/
.card {
   background-color: var(--rice-paper-bg-color);
   background-image: url(/images/rice-paper-2.png);
}

.default-ink {
   color: var(--default-ink);
}
